import React, {useState, useEffect} from 'react'
import { useSelector } from 'react-redux'
import styles from "./OrderBox.module.css"

export const OrderBox = (props) => {
    
    const [alphabetical, setAlphabetical] = useState(
        {ascendent: false,
        descendent: false})
    
    const [rating, setRating] = useState(
        {ascendent: false,
        descendent: false})
        
    
    const handleClick = event =>{
        console.log(event.target.attributes[2].value)
        if(event.target.attributes[2].value === "alphabetical"){
            setAlphabetical({...alphabetical, 
                [event.target.value] : event.target.checked})
        }
    }

    const limpiar = (alphabetical)=> {
        console.log(alphabetical)
        if(alphabetical.ascendent === true){
            setAlphabetical({...alphabetical,
                descendent : false
            })
        }
        if(alphabetical.descendent === true ){
                setAlphabetical({...alphabetical,
                    ascendent : false
                })
        }
    }

    useEffect(()=>
        limpiar(alphabetical),
        [alphabetical]
    )

//     useEffect(()=>
//     console.log(alphabetical),
//     [alphabetical]
// )
    
    // const handleCross = event =>{
    //   if(event.target.attributes[1].value === "genre"){
    //     let newGenres = state.genres.split(",").filter(g=>g !== event.target.value).join();
    //     setState({...state, genres: newGenres })
    //   }
    //   if(event.target.attributes[1].value === "source"){
    //     let newSource = state.source.split(",").filter(s=>s !== event.target.value).join();
    //     setState({...state, source: newSource })
    //   }
    // }

    return (
        <div id={styles.orderBox}>
            <h4>Order Box</h4>
            <div className={styles.nameContainer}>
                <label className={styles.label}>Order by name:</label>
                <label><input type="checkbox" id="cbox1" field="alphabetical" value="ascendent" onClick={handleClick} defaultChecked={alphabetical.ascendent}/>Ascendent</label>
                <label><input type="checkbox" id="cbox2" field="alphabetical" value="descendent" onClick={handleClick} defaultChecked={alphabetical.descendent}/>Descendent</label>
            </div>
            <div className={styles.nameContainer}>
                <label className={styles.label}>Order by rating:</label>
                <label><input type="checkbox" id="cbox1" field="rating" value="ascendent" defaultChecked={rating.ascendent} /> Top to bottom</label>
                <label><input type="checkbox" id="cbox2" field="rating" value="descendent" defaultChecked={rating.descendent}/> Bottom to Top</label>
            </div>
        </div>
    )
}

export default OrderBox;
