import React, { Component } from "react";
import { useEffect } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import { getVideogames } from "../../store/actions";



const Home = (props) =>  {
    
    console.log(props.videogames)
    useEffect(()=>
        props.getVideogames(),
        []
    ) 

    return (
      <div>
        <h2>Buscador</h2>
        <ul>
          {props.videogames && props.videogames.data.map(games =>
            <div key={games.id}>
              <Link to={`/videogames/${games.name}`}>
                <div>{games.name}</div>
              </Link>
            </div> 
          )}
        </ul>
      </div>
    );
  }


function mapStateToProps(state){
  return{
    videogames: state.gamesLoaded,
  }
}


// function mapDispatchToProps (dispatch) {
//   return {
//     addMovie: movie => dispatch (
//       addMovieFavorite(movie)),
//     getMovies: title => dispatch(getMovies(title)),
//     getMovieDetail: id => dispatch(getMovieDetail(id))
//   }
// }

// export default connect(mapStateToProps,mapDispatchToProps)(Buscador);

export default connect(mapStateToProps,{getVideogames})(Home);
