import React, { useState, useEffect, useRef } from "react";
import { useSelector, useDispatch } from "react-redux";
import styles from "./FilterBox.module.css";
import { filterGenres, filterSource } from "../../store/actions";

export const FilterBox = (props) => {
  const genresOpt = useSelector((store) => store.genres);
  const sourceOpt = useSelector((store) => store.source);

  const [state, setState] = useState({
    genres: "",
    source: "",
  });

  const handleClick = (event) => {
    if (event.target.attributes[1].value === "genre") {
      let newGenres = "";
      state.genres.includes(`${event.target.value}`) === true
        ? (newGenres = state.genres)
        : (newGenres = state.genres.concat(`${event.target.value},`));
      setState({ ...state, genres: newGenres });
    }

    if (event.target.attributes[1].value === "source") {
      console.log("entro a source");
      let newSource = "";
      state.source.includes(`${event.target.value}`) === true
        ? (newSource = state.source)
        : (newSource = state.source.concat(`${event.target.value},`));
      setState({ ...state, source: newSource });
    }
  };

  const handleCross = (event) => {
    console.log(event);
    if (event.target.attributes[0].value === "genre") {
      let newGenres = state.genres
        .split(",")
        .filter((g) => g !== event.target.value)
        .join();
      setState({ ...state, genres: newGenres });
    }
    if (event.target.attributes[0].value === "source") {
      let newSource = state.source
        .split(",")
        .filter((s) => s !== event.target.value)
        .join();
      setState({ ...state, source: newSource });
    }
  };

  const dispatch = useDispatch();

  useEffect(() => dispatch(filterGenres(state.genres)), [state.genres]);

  useEffect(() => dispatch(filterSource(state.source)), [state.source]);

  return (
    <div id={styles.container}>
      <div id={styles.genreContainer}>
        <div className={styles.title}>
          <h4>Filter by Genre</h4>
        </div>
        <div id={styles.listSelection}>
          <select
            id="genres"
            value={state.genres}
            size="3"
            className={styles.genresOpt}
          >
            {genresOpt &&
              genresOpt.map((genre) => (
                <option value={genre} onClick={handleClick} name="genre">
                  {genre}
                </option>
              ))}
          </select>
        </div>
        <div id={styles.filterOptBox}>
          {state.genres &&
            state.genres
              .split(",")
              .filter((g) => g.length > 0)
              .map((g) => (
                <div className={styles.options}>
                  <span className={styles.genresSelNames}>{g}</span>
                  <button onClick={handleCross} name="genre" value={g}>
                    X
                  </button>
                </div>
              ))}
        </div>
      </div>

      <div id={styles.sourceContainer}>
        <h4>Filter by source</h4>
        <select
          id="source"
          value={state.source}
          size="2"
          className={styles.genresOpt}
        >
          {sourceOpt &&
            sourceOpt.map((source) => (
              <option value={source} onClick={handleClick} name="source">
                {source}
              </option>
            ))}
        </select>
        <div className="filterButton">
          {state.source &&
            state.source
              .split(",")
              .filter((s) => s.length > 0)
              .map((s) => (
                <span className="filterButton">
                  <span className="filterButton">{s}</span>
                  <button onClick={handleCross} name="source" value={s}>
                    X
                  </button>
                </span>
              ))}
        </div>
      </div>
    </div>
  );
};

export default FilterBox;
