import React, {useState, useEffect} from 'react'
import { useSelector } from 'react-redux'
import styles from "./OrderBox.module.css"

export const OrderBox = (props) => {
    
    const [state, setState] = useState({
        alphabetical: {
            ascendent: false,
            descendent: false
        },
        rating: {
            ascendent: false,
            descendent: false
        }
    })    
    
    
    const handleClick = event =>{
    console.log(event.target.attributes[2].value)
    console.log(event.target.value)
    console.log(event.target.checked)
        setState({...state, 
            [`${event.target.attributes[2].value}.${event.target.value}`] : event.target.checked})
    }

    useEffect(()=>
    console.log(state),
    [])
    
    // const handleCross = event =>{
    //   if(event.target.attributes[1].value === "genre"){
    //     let newGenres = state.genres.split(",").filter(g=>g !== event.target.value).join();
    //     setState({...state, genres: newGenres })
    //   }
    //   if(event.target.attributes[1].value === "source"){
    //     let newSource = state.source.split(",").filter(s=>s !== event.target.value).join();
    //     setState({...state, source: newSource })
    //   }
    // }

    return (
        <div id={styles.orderBox}>
            <h4>Order Box</h4>
            <div className={styles.nameContainer}>
                <label className={styles.label}>Order by name:</label>
                <label><input type="checkbox" id="cbox1" field="alphabetical" value="ascendent" onClick={handleClick} defaultChecked={state.alphabetical.ascendent}/>Ascendent</label>
                <label><input type="checkbox" id="cbox2" field="alphabetical" value="descendent" defaultChecked={state.alphabetical.descendent}/>Descendent</label>
            </div>
            <div className={styles.nameContainer}>
                <label className={styles.label}>Order by rating:</label>
                <label><input type="checkbox" id="cbox1" field="rating" value="ascendent" defaultChecked={state.rating.ascendent} /> Top to bottom</label>
                <label><input type="checkbox" id="cbox2" field="rating" value="descendent" defaultChecked={state.rating.descendent}/> Bottom to Top</label>
            </div>
        </div>
    )
}

export default OrderBox;
